import React, {useState} from 'react';
import './App.css';
import {Todolist} from './Todolist';
import {v1} from 'uuid';

export type FilterValuesType = "all" | "active" | "completed";
export type todolistsType={
    id: string,
     title: string, 
     filter: FilterValuesType
}

function App() {

    // let [tasks, setTasks] = useState([
    //     {id: v1(), title: "HTML&CSS", isDone: true},
    //     {id: v1(), title: "JS", isDone: true},
    //     {id: v1(), title: "ReactJS", isDone: false},
    //     {id: v1(), title: "Rest API", isDone: false},
    //     {id: v1(), title: "GraphQL", isDone: false},
    // ]);
   

    let todolistID1=v1();
    let todolistID2=v1();

    let [todolists, setTodolists] = useState<Array<todolistsType>>([
        {id: todolistID1, title: 'What to learn', filter: 'active'},
        {id: todolistID2, title: 'What to buy', filter: 'all'},
    ])



    let [tasks, setTasks] = useState({
        [todolistID1]:[
            {id: v1(), title: "HTML&CSS", isDone: true},
            {id: v1(), title: "JS", isDone: true},
            {id: v1(), title: "ReactJS", isDone: false},
            {id: v1(), title: "Rest API", isDone: false},
            {id: v1(), title: "GraphQL", isDone: false},
        ],
        [todolistID2]:[
            {id: v1(), title: "HTML&CSS2", isDone: true},
            {id: v1(), title: "JS2", isDone: true},
            {id: v1(), title: "ReactJS2", isDone: false},
            {id: v1(), title: "Rest API2", isDone: false},
            {id: v1(), title: "GraphQL2", isDone: false},
        ]
    });




    function addTask(listId:string, title: string) {
        let task = {id: v1(), title: title, isDone: false};
        // let newTasks = [task, ...tasks];
        setTasks({...tasks,[listId]:[task,...tasks[listId]]});

    }

    function changeStatus(listId:string,taskId: string, isDone: boolean) {
        // let task = tasks.find(t => t.id === taskId);
        // if (task) {
        //     task.isDone = isDone;
        // }

        // setTasks([...tasks]);

        setTasks({...tasks,[listId]:tasks[listId].find(t=>isDone)})
    }


  
    function removeTask(listId:string,id: string) {
setTasks({...tasks,[listId]:tasks[listId].filter(el=>el.id !== id)})

    }

    function changeFilter(value: FilterValuesType, listId:string) {
                        // let findedList=todolists.find((el)=>
                        // //этот кусок ищет лист, который нам приходит в массиве данных
                        //     el.id===listId
                        // )
                        // //нашел, а значит равно правде
                        // if(findedList){
                        //     //присваиваем новое значение
                        //     findedList.filter=value
                        //     //оно перезаписывается за счет обмана
                        //     setTodolists([...todolists])
                        // }
                        setTodolists(todolist.map(el=>listId))
    }

    

    return (
        <div className="App">

            {
                todolists.map((el:any)=>{ 

                    
                    let tasksForTodolist=tasks[el.id]

                    if (el.filter === "active") {
                        tasksForTodolist=tasks[el.id].filter(t => t.isDone === false);
                    }
                    if (el.filter === "completed") {
                        tasksForTodolist=tasks[el.id].filter(t => t.isDone === true);
                    }


                  
  
                
                    return(
                    <Todolist
                    key={el.id}
                    title={el.title}
                    listId={el.id}
                    tasks={tasksForTodolist}
                    removeTask={removeTask}
                    changeFilter={changeFilter}
                    addTask={addTask}
                    changeTaskStatus={changeStatus}
                    filter={el.filter}
          />
                )
                  
                })
            }
          
        </div>
    );
}

export default App;


